import React, { Component } from 'react';

export class FetchData extends Component {
    static displayName = FetchData.name;

    constructor(props) {
        super(props);
        this.state = { players: [], loading: true };
    }

    componentDidMount() {
        this.populateWeatherData();
    }

    static renderForecastsTable(players) {
        return (
            <table className='table table-striped' aria-labelledby="tabelLabel">
                <thead>
                    <tr>
                        <th>Player Name</th>
                        <th>Card 1</th>
                        <th>Card 2</th>
                        <th>Card 3</th>
                        <th>Card 4</th>
                        <th>Card 5</th>
                    </tr>
                </thead>
                <tbody>
                    {players.map(player =>
                        <tr key={player.playerName}>
                            <td>{player.playerName}</td>
                            {player.playerHand.cards.map(card =>
                                <td>{card.CardSuit} {card.cardNumber}</td>)}

                        </tr>
                    )}
                </tbody>
            </table>
        );
    }

    render() {
        let contents = this.state.loading
            ? <p><em>Loading...</em></p>
            : FetchData.renderForecastsTable(this.state.players);

        return (
            <div>
                <h1 id="tabelLabel" >Poker Game</h1>
                <p>This component demonstrates fetching data from the server.</p>
                {contents}
            </div>
        );
    }

    async populateWeatherData() {
        const response = await fetch('pokergame');
        const data = await response.json();
        this.setState({ players: data, loading: false });
    }
}
